/
CREATE OR REPLACE TRIGGER LMS_BORROWEDBOOKS_TRIGGER
BEFORE INSERT ON LMS_BORROWEDBOOKS
FOR EACH ROW
BEGIN
    SELECT BORROW_ID_SEQ.NEXTVAL INTO :NEW.BORROW_ID FROM DUAL;
END;